    private void btnIniActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIniActionPerformed
        if ("".equals(txtUN.getText())) {
            JOptionPane.showMessageDialog(null, "Falta usuario");
        } else if ("".equals(txtPW.getText())) {
            JOptionPane.showMessageDialog(null, "Falta Contrase√±a");
        }
        int res = con.login(txtUN.getText(), txtPW.getText());
        nombre=txtUN.getText();
        Menu m = new Menu();
        if (res == 1) {
            m.setVisible(true);
            dispose();
        } else if (res == 2) {
            m.setVisible(true);
            dispose();
            nombre=txtUN.getText();
        }


    }//GEN-LAST:event_btnIniActionPerformed

